window.onload=function(){const botao=document.querySelector('#dropdown-icon');const span=document.querySelector('#dropdown-icon span');const menu=document.getElementById('menu');const navbar=document.getElementById('navbar');const li=document.querySelectorAll("#menu li a");const skills=document.getElementById('habilidades');const header_height=document.getElementById('navbar').offsetHeight;let links=document.links;let html=document.documentElement;let body=document.body;for(var i=0;i<links.length;i++){links[i].onclick=function(e){if(menu.className=='dropdown'){menu.classList.remove('dropdown');span.classList.remove('ativo')}
let scrollTop=Math.round(body.scrollTop||html.scrollTop);if(this.hash!=''){e.preventDefault();let hashEl=document.getElementById(this.hash.substring(1));let hashTop=0;let obj=hashEl;while(obj.offsetParent){hashTop+=obj.offsetTop;obj=obj.offsetParent}
hashTop=Math.round(hashTop);scroll(scrollTop,hashEl,this.hash)}}};function scroll(from,to,hash){let timeInterval=10;let prevScroll;let incremento=(to>from)?10:-10;let scrollPixel=setInterval(function(){let scrollTop=Math.round(body.scrollTop||html.scrollTop);if(prevScroll==scrollTop||(to>from&&scrollTop>=to)||(to<from&&scrollTop<=to)){clearInterval(scrollPixel);window.location.hash=hash}else{body.scrollTop+=incremento;html.scrollTop+=incremento;prevScroll=scrollTop}},timeInterval)};botao.addEventListener('click',function(){if(span.className!='humburger-icon ativo'){span.classList.add('ativo');menu.classList.add('dropdown')}else{span.classList.remove('ativo');menu.classList.remove('dropdown')}});let desceu=0;let subiu=window.pageYOffset;window.onscroll=function(){desceu=window.pageYOffset;if(subiu<desceu&&subiu>header_height){navbar.classList.add('sumiu');subiu=window.pageYOffset}else{navbar.classList.remove('sumiu');subiu=window.pageYOffset}}}
